import React from "react";
import Block from "./Block";
// ADD CODE HERE

class Feed extends React.Component {
  state = {
    // ADD CODE HERE
    showopt: false,
    blocks: [new Block("aaa")],
  };

  addBlock = (color) => {
    let blocks = this.state.blocks.slice();
    console.log(blocks);
    blocks.unshift(new Block(color));
    this.setState({
      // ADD CODE HERE
      //blocks: blocks,
    });
  };

  pickColorlayout = () => {
    if (this.state.showopt === false) {
      this.setState({
        showopt: true,
      });
    } else {
      this.setState({
        showopt: false,
      });
    }
  };

  render() {
    return (
      <div className="mainfeed">
        <div className="input">{/* ADD CODE HERE */ this.state.blocks}</div>
        <div>
          <button class="button" onClick={this.pickColorlayout}>
            + Post Block
          </button>
        </div>
        {this.state.showopt ? (
          <div className="pickblock">
            <div>
              <button
                className="red"
                onClick={() => this.addBlock("rgb(134, 5, 5)")}
              ></button>
              <button
                className="blue"
                onClick={() => this.addBlock("rgb(21, 0, 141)")}
              ></button>
            </div>
            <div>
              <button
                className="green"
                onClick={() => this.addBlock("rgb(59, 110, 0)")}
              ></button>
              <button
                className="purple"
                onClick={() => this.addBlock("rgb(58, 0, 97)")}
              ></button>
            </div>
          </div>
        ) : null}
      </div>
    );
  }
}

export default Feed;
